function validateInputNumbers(e){return e.parsley({errorsWrapper:'<div class="text-danger text-center py-3 font-weight-bold"></div>',errorTemplate:"<span></span>"}).validate(),!!e.parsley().isValid()}$(document).ready(function(){$("#draw-form");$("#number-input").on("submit",function(e){var a=$(this);if(e.preventDefault(),validateInputNumbers(a)){var t=a.serialize();$.ajax({url:"submit",type:"POST",data:t,success:function(e){e.success?a.append(alertBox("success",e.success)):e.verificationFailed?a.append(alertBox("danger",e.verificationFailed)):e.noResults&&a.append(alertBox("danger",e.noResults))},error:function(){a.append(alertBox("danger","Dogodila se pogre≈°ka, probajte ponovo!"))},complete:function(){var e=a.attr("id");$("#"+e+" .alert").fadeOut(5e3)}})}}),$(".error").delay(3e3).fadeOut(),App.init()});var App={init:function(){removeInputIcons("input")}};
function removeInputIcons(e){$(e).checkboxradio({icon:!1})}function alertBox(e,r){var t="";return t+='<div class="text-center alert alert-'+e+' fade show" role="alert">',t+=r,t+="</div>"}