var App={init:function(){removeInputIcons("input"),handleNumbersInput()}};$(document).ready(function(){App.init()});
function removeInputIcons(e){$(e).checkboxradio({icon:!1})}function alertBox(e,r){var t="";return t+='<div class="text-center alert alert-'+e+' fade show" role="alert">',t+=r,t+="</div>"}
var handleNumbersInput=function(){$("#number-input").on("submit",function(e){var a=$(this);if(e.preventDefault(),validateInputNumbers(a)){var n=a.serialize();$.ajax({url:"submit",type:"POST",data:n,success:function(e){e.winningNumbers?a.append(alertBox("success",e.winningNumbers)):e.verificationFailed?a.append(alertBox("danger",e.verificationFailed)):e.noResults?a.append(alertBox("danger",e.noResults)):e.success&&a.append(alertBox("success",e.success))},error:function(){a.append(alertBox("danger","Dogodila se pogre≈°ka, probajte ponovo!"))},complete:function(){var e=a.attr("id");$("#"+e+" .alert").fadeOut(5e3)}})}})};function validateInputNumbers(e){return e.parsley({errorsWrapper:'<div class="text-danger text-center py-3 font-weight-bold"></div>',errorTemplate:"<span></span>"}).validate(),!!e.parsley().isValid()}