var App={init:function(){removeInputIcons("input"),handleNumbersInput()}};$(document).ready(function(){App.init()});
function removeInputIcons(e){$(e).checkboxradio({icon:!1})}function alertBox(e,r){var t="";return t+='<div class="text-center alert alert-'+e+' fade show" role="alert">',t+=r,t+="</div>"}
var handleNumbersInput=function(){$("#number-input").on("submit",function(e){var n=$(this);if(e.preventDefault(),validateInputNumbers(n)){var a=n.serialize();$.ajax({url:"submit",type:"POST",data:a,beforeSend:function(){$(".main-results").removeClass("correct")},success:function(e){e.winningNumbers?(n.append(alertBox("success",e.winningNumbers)),markWinningNumbers($("input:checked").map(function(e,n){return n.value}).get())):e.verificationFailed?n.append(alertBox("danger",e.verificationFailed)):e.noResults?n.append(alertBox("danger",e.noResults)):e.notAwinningTicket&&n.append(alertBox("warning",e.notAwinningTicket))},error:function(){n.append(alertBox("danger","Dogodila se pogre≈°ka, probajte ponovo!"))},complete:function(){var e=n.attr("id");$("#"+e+" .alert").fadeOut(5e3)}})}})};function validateInputNumbers(e){return e.parsley({errorsWrapper:'<div class="text-danger text-center py-3 font-weight-bold"></div>',errorTemplate:"<span></span>"}).validate(),!!e.parsley().isValid()}function markWinningNumbers(n){$.each($(".main-results .digit"),function(e,a){var t=$(a).html();$.each(n,function(e,n){n==t&&$(a).parent().addClass("correct")})})}